def on_sort():
    sorted_pokemons = sorted(pokemons)
    for pokemon in sorted_pokemons:
        listbox_sort.insert(tk.END, pokemon)


def on_sort_reverse():
    sorted_pokemons = sorted(pokemons, reverse=True)
    for pokemon in sorted_pokemons:
        listbox_sort_reverse.insert(tk.END, pokemon)


window = tk.Tk()
window.title('Pokemon Sort')
window.geometry('800x600')

# button section

frame_buttons = tk.Frame(window)
frame_buttons.pack(pady=20)

button_sort = tk.Button(frame_buttons, text='Sort', width=20, height=2, font=('Arial', 18, 'normal'))
button_sort.pack(side='left', padx=20)
button_sort['command'] = on_sort

button_sort_reverse = tk.Button(frame_buttons, text='Sort Desc', width=20, height=2, font=('Arial', 18, 'normal'))
button_sort_reverse.pack(side='left', padx=20)
button_sort_reverse['command'] = on_sort_reverse

# listbox section

frame_lists = tk.Frame(window)
frame_lists.pack(expand=True, fill=tk.BOTH)

listbox_origin = tk.Listbox(frame_lists)
listbox_origin.pack(side='left', expand=True, fill=tk.BOTH)

listbox_sort = tk.Listbox(frame_lists)
listbox_sort.pack(side='left', expand=True, fill=tk.BOTH)

listbox_sort_reverse = tk.Listbox(frame_lists)
listbox_sort_reverse.pack(side='left', expand=True, fill=tk.BOTH)

for pokemon in pokemons:
    listbox_origin.insert(tk.END, pokemon)

tk.mainloop()
